          summarise(First = min(ReceiptDate), Max = max(ReceiptDate), Donations = n_distinct(ContributionID), Total = sum(Amount,na.rm = TRUE)) %>% 
          ungroup() %>% 
          arrange(desc(Total)) %>% select(Donations,Total,everything())
     
     
     chk4 <- cheque %>% group_by(Employer,EmployerName,Industry,FullName,ReceiptDate) %>%
          summarise(Total = sum(Amount,na.rm = TRUE)) %>%
          ungroup() %>%
          arrange(ReceiptDate, desc(Total))
     
