dnr <- function(x, emp = FALSE) {
     var <- x
     w <- filter(dfx, FullName %in% var) 
     
     wx <- w %>% group_by(FullName) %>% 
          summarise(Donations = n_distinct(ContributionID),
                    Total = sum(Amount, na.rm = TRUE)) %>% 
          ungroup() %>% 
          arrange(FullName)
     
     if(emp == FALSE) {
          wf <- w %>% group_by(FullName,Address) %>% 
               summarise(Donations = n_distinct(ContributionID),
                         Total = sum(Amount, na.rm = TRUE),
                         Min = min(ReceiptDate), 
                         Max = max(ReceiptDate)) %>% 
               ungroup() %>% 
               arrange(desc(Total))
     } else {
          wf <- w %>% group_by(FullName,Address,EmployerName) %>% 
               summarise(Donations = n_distinct(ContributionID),
                         Total = sum(Amount, na.rm = TRUE),
                         Min = min(ReceiptDate), 
                         Max = max(ReceiptDate)) %>% 
               ungroup() %>% 
               arrange(desc(Total))
     }
     
     return(head(wf, 20))
     return(head(wx, 20))
}


